# creates the absolutely necessary tables for the website to function
create table page_handlers (id int unsigned not null auto_increment unique primary key comment 'primary key used for identification', handler_name text comment 'human readable name of the page handler', handler_module int not null comment 'identifier for the module providing the handler');
create table modules (id int unsigned not null auto_increment unique primary key comment 'primary key used for identification', module_name text not null unique comment 'machine readable name for the module', module_path text comment 'name of the modules package provided correctly configured __init__.py or file that is reachable by (omit ".py")');
create table content_types(id int unsigned not null auto_increment unique primary key comment 'primary key used for identification', content_type_name text not null comment 'name of the content type', page_handler int not null comment 'id of the associated page handler type', description text comment 'human readable content type description');
create table page_fields (id int unsigned not null auto_increment unique primary key comment 'primary id for machine convenience', field_name text not null, content_type int not null comment 'id of associated content type', description text comment 'human readable field type description');
create table page_fields_instances (id int unsigned not null auto_increment unique primary key comment 'primary id used to identify field', field_type int not null comment 'id of associated field type', field_contents text not null comment 'contents of the field');
create table alias (id int unsigned not null auto_increment unique primary key comment 'unique identifier', source_id int not null comment 'page id of the source page', alias text not null unique comment 'page alias');
create table created_tables (id int unsigned not null auto_increment unique primary key, created_table text unique not null, creating_module_name text not null, creating_module_id int unsigned not null);
